#!/usr/bin/env bash

PATH=/usr/local/bin/:$PATH

btc_icon=''
eth_icon=''
ltc_icon=''
xmr_icon=''

btc_price=$(curl -s -H "CB-Version: 2015-04-08" "https://api.coinbase.com/v2/prices/BTC-EUR/buy" | python -c "import sys, json; print(json.load(sys.stdin)['data']['amount'])")
eth_price=$(curl -s -H "CB-Version: 2015-04-08" "https://api.coinbase.com/v2/prices/ETH-EUR/buy" | python -c "import sys, json; print(json.load(sys.stdin)['data']['amount'])")
ltc_price=$(curl -s -H "CB-Version: 2015-04-08" "https://api.coinbase.com/v2/prices/LTC-EUR/buy" | python -c "import sys, json; print(json.load(sys.stdin)['data']['amount'])")
xmr_price=$(curl -s curl 'https://min-api.cryptocompare.com/data/price?fsym=XMR&tsyms=EUR' | jq '.[]')

echo "$btc_price | templateImage=$btc_icon"
echo "$eth_price | templateImage=$eth_icon"
echo "$ltc_price | templateImage=$ltc_icon"
echo "$xmr_price | templateImage=$xmr_icon"
